const previewVideo = document.querySelector('.previewVideo video')
const btnPlayVideo = document.querySelector('.previewVideo__play')

btnPlayVideo.addEventListener('click', function () {

	btnPlayVideo.classList.add('previewVideo__play_hidden')
	previewVideo.play()
	previewVideo.setAttribute('controls', 'controls')
})


const images = document.querySelectorAll('.photos__pic')
const btnPrev = document.querySelector('.btnArrowLeft')
const btnNext = document.querySelector('.btnArrowRight')
let current = 0

btnPrev.addEventListener('click', function () {
	console.log(btnPrev)
	images[current].classList.remove('photos__pic_show')
	current--

	if (current < 0) {
		current = images.length - 1
	}
	images[current].classList.add('photos__pic_show')

})
